        dispatch(getMe())
        return () => {
            dispatch(reset())
        }
    },[dispatch])
    if(isLoading){
        return (
            <div className='sigepec-big-load'>
                <div className='load'></div>
            </div>
        )
    }
    if(isError){
        return (

            <EmptySection Illustration={SadIllustration}>
                {message}
            </EmptySection>
        )
    }
