                        </div>
                    </div>

                    <div className='sigepec-page-formulaire'>
                            {
                                isEditError && (
                                    <NoticeForm status={"danger"} Icon={DangerIcon} typo={true} title={"Oupss!!"}>
                                        {message}
                                    </NoticeForm>
                                )
                            }
